import { useState } from "react";


function App(){
    const [data,setData]=useState([])
    fetch("  http://localhost:3009/project")
    .then((res)=>{
        return res.json()
    })
    .then((resp)=>{
        setData(resp)
    },[])
    return(
        <div>
                <div style={{display:"grid",gridTemplateColumns:"auto auto auto auto auto",gridGap:"20px"}}>
                   {data.map((ourdata)=>(
                    <div>
                      <div style={{width:"250px",height:"400px"}}>
                        <img src={ourdata.img} style={{width:"200px", height:"200px"}}/>
                        <h3>{ourdata.name}</h3>
                        <p>{ourdata.manufacturer}</p>
                        <p>{ourdata.production}</p>
                        <p>{ourdata.price}</p>
                        <span className="btn  btn-primary" >BUY THIS</span>
                        <span className="btn btn-success">ADD CART</span>
                      </div>
                      </div>
                   ))}
                </div>
        </div>
    )
}
export default App;
