import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./resp.css"


function AddCart() {
    const [data, setData] = useState([])
    const [total, setTotal] = useState(0)
    const [inn, setInn] = useState(1)

    useEffect(() => {
        fetch("http://localhost:3009/seconddata")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)
                console.log(resp)
            })
    }, [])


    const dele = (g) => {
        fetch("http://localhost:3009/seconddata/" + g, {
            method: "DELETE"
        })
        window.location.reload()
    }

    useEffect(() => {
        fetch("http://localhost:3009/seconddata")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)
                const total = resp.reduce((acc, item) => acc + parseFloat(item.price), 0)
                setTotal(total)
            })
    }, [])



    const valueinc = () => {
        setInn(inn+1)
    }
    const valuedec=()=>{
        if(inn<=1){
          return false
        }
        setInn(inn-1)
      }









    return (

        <div>
            <div style={{ display: "flex", justifyContent: "space-between",paddingTop:"10px" }}>
                
                <h4 style={{color:"green"}}>
                    Total AddCart Price:{total} Cr
                </h4>

                <Link to="/App" style={{ marginRight: "70px" }}>
                    <button className="btn btn-danger">back</button>
                </Link>
            </div>
            <div className="add" style={{ display: "grid", gridTemplateColumns: "auto auto auto auto", gridGap: "20px", marginTop: "20px", marginLeft: "30px" }}>
                {data.map((ourdata) => (
                    <table class="table">
                    <thead>
                      <tr>
                        <th scope="col">id</th>
                        <th scope="col">First</th>
                        <th scope="col"></th>
                        <th scope="col">Handle</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <th scope="row">1</th>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                      </tr>
                      <tr>
                        <th scope="row">2</th>
                        <td>Jacob</td>
                        <td>Thornton</td>
                        <td>@fat</td>
                      </tr>
                      <tr>
                        <th scope="row">3</th>
                        <td colspan="2">Larry the Bird</td>
                        <td>@twitter</td>
                      </tr>
                    </tbody>
                  </table>
                ))}



            </div>
        </div>
    )
}
export default AddCart;
