import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./resp.css"


function AddCart() {
    const [data, setData] = useState([])
    const [total, setTotal] = useState(0)
    const [inn, setInn] = useState(1)

    useEffect(() => {
        fetch("http://localhost:3009/seconddata")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)
                console.log(resp)
            })
    }, [])


    const dele = (g) => {
        fetch("http://localhost:3009/seconddata/" + g, {
            method: "DELETE"
        })
        window.location.reload()
    }

    useEffect(() => {
        fetch("http://localhost:3009/seconddata")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)
                const total = resp.reduce((acc, item) => acc + parseFloat(item.price), 0)
                setTotal(total)
            })
    }, [])



    const valueinc = () => {
        setInn(inn + 1)
    }
    const valuedec = () => {
        if (inn <= 1) {
            return false
        }
        setInn(inn - 1)
    }









    return (

        <div>
            <div style={{ display: "flex", justifyContent: "space-between", paddingTop: "10px" }}>

                <h4 style={{ color: "green" }}>
                    Total AddCart Price:{total} Cr
                </h4>

                <Link to="/App" style={{ marginRight: "70px" }}>
                    <button className="btn btn-danger">back</button>
                </Link>
            </div>
            <div className="add">
                <table class="table">
                    <thead>
                        <tr>
                            <th scope="col">id</th>
                            <th scope="col">img</th>
                            <th scope="col">name</th>
                            <th scope="col">manufacturer</th>
                            <th scope="col">stock</th>
                            <th scope="col">price</th>
                            <th scope="col">Remove data</th>
                        </tr>
                    </thead>
                </table>
                {data.map((ourdata) => (
               <table>
                    <tbody >
                        <tr>
                            <td >
                                <h6>{ourdata.id}</h6>
                                <img src={ourdata.img} alt="k" style={{ width: "80px", height: "80px", borderRadius: "8px" }} />
                                <h6>{ourdata.name}</h6>
                                <h6>{ourdata.manufacturer}</h6>
                                <h6> <i class="fa-solid fa-indian-rupee-sign"></i> {ourdata.price}</h6>
                                <p className="btn btn-danger" onClick={() => { dele(ourdata.id) }}>Remove</p>
                            </td>
                        </tr>
                    </tbody>
                    </table>
                ))}



            </div>
        </div>
    )
}
export default AddCart;
