import React, { useState } from 'react';

function Counter() {
  const [count, setCount] = useState(0);
  const [valueToCompare, setValueToCompare] = useState(5); // Comparison value
  const [increaseBy, setIncreaseBy] = useState(1); // Value to increase count by
  const [decreaseBy, setDecreaseBy] = useState(1); // Value to decrease count by

  const increaseCount = () => {
    if (count < valueToCompare) {
      setCount(prevCount => prevCount + increaseBy);
    }
  };

  const decreaseCount = () => {
    if (count > 0) {
      setCount(prevCount => prevCount - decreaseBy);
    }
  };

  return (
    <div>
      <p>Count: {count}</p>
      <div>
        <label>
          Value to Compare:
          <input type="number" value={valueToCompare} onChange={(e) => setValueToCompare(+e.target.value)} />
        </label>
      </div>
      <div>
        <label>
          Increase By:
          <input type="number" value={increaseBy} onChange={(e) => setIncreaseBy(+e.target.value)} />
        </label>
      </div>
      <div>
        <label>
          Decrease By:
          <input type="number" value={decreaseBy} onChange={(e) => setDecreaseBy(+e.target.value)} />
        </label>
      </div>
      <div>
        <button onClick={increaseCount}>Increase Count</button>
        <button onClick={decreaseCount}>Decrease Count</button>
      </div>
    </div>
  );
}

export default Counter;
