import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./resp.css"


function AddCart() {
    const [data, setData] = useState([])
    const [total, setTotal] = useState(0)
    const [inn, setInn] = useState(1)

    useEffect(() => {
        fetch("http://localhost:3009/seconddata")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)
                console.log(resp)
            })
    }, [])


    const dele = (g) => {
        fetch("http://localhost:3009/seconddata/" + g, {
            method: "DELETE"
        })
        window.location.reload()
    }

    useEffect(() => {
        fetch("http://localhost:3009/seconddata")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)
                const total = resp.reduce((acc, item) => acc + parseFloat(item.price), 0)
                setTotal(total)
            })
    }, [])



    const valueinc = () => {
        setInn(inn+1)
    }
    

    const valuedec=()=>{
        if(inn<=0){
          return false
        }
    
        setInn(inn-1)
      }









    return (

        <div>
            <div style={{ display: "flex", justifyContent: "space-between",paddingTop:"10px" }}>
                
                <h4 style={{color:"green"}}>
                    Total AddCart Price:{total} Cr
                </h4>

                <Link to="/App" style={{ marginRight: "70px" }}>
                    <button className="btn btn-danger">back</button>
                </Link>
            </div>
            <div className="add" style={{ display: "grid", gridTemplateColumns: "auto auto auto auto", gridGap: "20px", marginTop: "20px", marginLeft: "30px" }}>
                {data.map((ourdata) => (
                    <div>
                        <div className="card" style={{ width: "250px", height: "330px", textAlign: "center", boxShadow: "lightblue 4px 5px 5px 4px" }} >
                            <img src={ourdata.img} alt="k" style={{ width: "220px", height: "200px", borderRadius: "8px", marginLeft: "14px", marginTop: "20px" }} />
                            <h4>{ourdata.name}</h4>
                            <h5>{ourdata.manufacturer}</h5>
                            <h5> <i class="fa-solid fa-indian-rupee-sign"></i> {ourdata.price}</h5>
                            <div> 
                            <ul style={{marginLeft:"70px"}} className="pagination">
                                <li className="page-item">
                                    <a  className="page-link" onClick={valuedec}>-</a>
                                </li>
                                <li className="page-item">
                                    <a  className="page-link">{inn}</a>
                                </li>
                                <li className="page-item">
                                    <a   className="page-link" onClick={valueinc}>+</a>
                                </li>
                            </ul>
                          </div>
                            <p className="btn btn-danger" onClick={() => { dele(ourdata.id) }}>Remove</p>
                        </div>
                    </div>
                ))}



            </div>
        </div>
    )
}
export default AddCart;
